import React, { useState, useEffect } from 'react';
import Web3 from 'web3';
import SimpleStorageABI from './SimpleStorage.json';

const ganacheUrl = 'http://127.0.0.1:7545';
const SimpleStorageAddress = '0xa5dE248Dc33104D7B0A4F3D8f23ECf6592e95ffc';

function App() {
  const [data, setData] = useState('');
  const [web3, setWeb3] = useState(null);
  const [contract, setContract] = useState(null);

  useEffect(() => {
    const initializeWeb3 = async () => {
      try {
        const provider = new Web3.providers.HttpProvider(ganacheUrl);
        const web3Instance = new Web3(provider);
        setWeb3(web3Instance);
        const simpleStorageContract = new web3Instance.eth.Contract(
          SimpleStorageABI,
          SimpleStorageAddress
        );
        setContract(simpleStorageContract);

        console.log('Connected to blockchain network'); 
      } catch (error) {
        console.error('Error initializing web3:', error);
      }
    };
    initializeWeb3();
  }, []);

  const fetchData = async () => {
    try {
      const result = await contract.methods.getData().call();
      setData(result);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  const setDataOnChain = async () => {
    try {
      const accounts = await web3.eth.getAccounts();
      await contract.methods.setData(123).send({ from: accounts[0] });
      fetchData(); 
    } catch (error) {
      console.error('Error setting data:', error);
    }
  };

  return (
    <div>
      <h1>Blockchain App</h1>
      <button onClick={fetchData}>Fetch Data</button>
      <p>Data from Smart Contract: {data}</p>
      <button onClick={setDataOnChain}>Set Data</button>
    </div>
  );
}

export default App;
